import React from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity, ScrollView } from 'react-native';
//import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';


export default function Home ({navigation}) {
  const pressHandler1 = () => {
    navigation.push('PatientLog')
  }

  const pressHandler = () => {
    navigation.push('StaffLog')
  }

// export default function Home () {
//     const pressHandler = () => {
//         navigation.push('Login')
// }
    return (
      <View style={styles.Home}>
        <ScrollView showsVerticalScrollIndicator={false}>
          <Text style={styles.header}>Heal Port</Text>

          <TouchableOpacity style={styles.button} onPress = { pressHandler1}>
              <Text style={styles.btntext}>Patient Login</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button} onPress = { pressHandler}>
              <Text style={styles.btntext}>Staff Login</Text>
          </TouchableOpacity>

        </ScrollView>
      </View>
    );
}

const styles = StyleSheet.create({
  Home: {
    flex: 1,
    justifyContent: 'center',
    // width: wp('70%'),
    // height: hp('80%'),
    // marginTop: 50,
    paddingLeft: 30,
    paddingRight: 30,
    backgroundColor: '#990099',
  },
  header: {
      fontSize: 40,
      textAlign: 'center',
      color: '#fff',
      marginBottom: 100,
      marginTop: 50,
      // paddingBottom: 5,
  },
  textinput: {
      // alignSelf: 'stretch',
      height: 40,
      marginBottom: 30,
      color: '#fff',
      borderBottomColor: '#f8f8f8',
      borderBottomWidth: 1,

    
  },
  button: {
    alignSelf: 'stretch',
    alignItems: 'center',
    padding: 12,
    backgroundColor: '#6B07A1',
    marginTop: 10,
    marginBottom: 50,
    borderRadius: 200,
},
  btntext: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 25,
},
});
