import React,  { useState, useLayoutEffect } from 'react'
import { KeyboardAvoidingView, StyleSheet, Text, View } from 'react-native'
import { StatusBar } from 'expo-status-bar';
import { Image, Input, Button } from 'react-native-elements';
import { TouchableOpacity } from 'react-native-gesture-handler';



const LoginScreen = ({ navigation }) => {

    const [ email, setEmail ] = useState('');
    const [ password, setPassword ] = useState('');

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "WELCOME TO HEAL PORT",
        });
    }, [navigation]);

    const signIn = () => {}

    return (
        <KeyboardAvoidingView behavior="padding" style={styles.container}>
           <StatusBar style={"light"} />
           <Image source={{
            //    uri: "https://drive.google.com/file/d/18bgC39DakmrOnMMKsC1ieL3Nn_2aV8ZQ/view?usp=sharing",
                uri: 'https://cesie.org/media/heal-logo.jpg', 
           }}
               style={{ width: 300, height: 200 }}
            />

            <View style={styles.inputContainer}>
                <Input 
                    placeholder="Email"
                    autoFocus
                    type="email"
                    value={email}
                    onChangeText={(text) => setEmail(text)}
                />

                <Input 
                    placeholder="Password" 
                    secureTextEntry
                    type="password"
                    value={password}
                    onChangeText={(text) => setPassword(text)}
                    onSubmitEditing={signIn}
                />
            </View>

            <TouchableOpacity 
                activeOpacity={0.5} 
                style={styles.buttonLogin}
                onPress={signIn}
            >
                <Text style={styles.buttonTextLogin}>Login</Text>
            </TouchableOpacity>

            <TouchableOpacity 
                style={styles.buttonRegister}
                onPress={() => navigation.navigate("Register")}
            >
                <Text style={styles.buttonTextRegister}>Register</Text>
            </TouchableOpacity>

           <Text style={{ marginTop: 60, color: 'gray', fontSize: 10}}>v1.0001</Text>

        </KeyboardAvoidingView>
    )
}

export default LoginScreen;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: 'center',
        padding: 10,
        backgroundColor: 'white',
    },
    inputContainer: {
        width: 300,
    },
    buttonLogin: {
        width: 200,
        marginTop: 10,
        textAlign: "center",
        borderRadius: 10,
        padding: 10,
        borderWidth: 3,
        borderColor: "#42f554",
        backgroundColor: "#42f554",
    },
    buttonRegister: {
        width: 200,
        marginTop: 10,
        textAlign: "center",
        borderRadius: 10,
        padding: 10,
        backgroundColor: "#fff",
        borderWidth: 3,
        borderColor: "#42f554",
    },
    buttonTextLogin: {
        textAlign: 'center',
        fontSize: 15,
        color: 'white',
        fontWeight: 'bold',
    },
    buttonTextRegister: {
        textAlign: 'center',
        fontSize: 15,
        color: '#42f554',
        fontWeight: 'bold',
    }
});
